-- Added missing spawn of creatures 15316 (Qiraji Scorpion) and 15317 (Qiraji Scarab) in Temple of Ahn'Qiraj
-- This closes #168 
-- Source: YTDB

SET @GUID := 151208;

DELETE FROM `creature` WHERE (`guid` BETWEEN @GUID + 1 AND @GUID + 111) OR (`id` IN (15316, 15317));

-- Fixed spawn time, roaming of creatures 15317 (Qiraji Scorpion) and 15316 (Qiraji Scarab)
UPDATE `creature` SET `spawndist` = 5, `MovementType` = 1, `spawntimesecs` = (5 * 60) WHERE `id` IN (15316, 15317);
-- Fixed health of creature 15317 (Qiraji Scorpion)
UPDATE `creature` SET `curhealth` = 6104 WHERE `id` = 15317;
UPDATE `creature_template` SET `MinLevelHealth` = 6104, `MaxLevelHealth` = 6104 WHERE `Entry` = 15317;
